Object.Field:
    	import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
		import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;
        @GeneratorDelegateClass(componentType=DatastoreComponent.COMPONENT_TYPE_NAME, descriptionKey="GeneratorDelegateClass.HiveAppend")
    	public class IKM_Hive_Append extends HiveBaseKM {
    	    // class constructor; initialization is done in setBinding method
    	    public IKM_Hive_Append() {}

            // Declare KM level variables
            protected AbstractSyntaxTree intoTargetAST
            protected AbstractSyntaxTree targetTableAST
            protected boolean option_TRUNCATE                 = false
            protected OdiDataStore sourceDataStore, targetDataStore;
            protected String sourceSchemaName;

            void setBinding( Binding binding ) {
            	super.setBinding( binding )
                option_TRUNCATE                   = isOptionTrue( "TRUNCATE" )

                Object tgtCompBondObj = this.physicalNode.getLogicalComponent().getBoundObject();
                targetDataStore = ((oracle.odi.domain.model.OdiDataStore) tgtCompBondObj).getRealObject()

                List<MapPhysicalNode> upStreamNodes = this.physicalNode.getUpstreamConnectedNodes();
                while (!upStreamNodes.get(0).isSourceNode()){
                  upStreamNodes = upStreamNodes.get(0).getUpstreamConnectedNodes();
                }
                MapPhysicalNode parentNode = upStreamNodes.get(0);
                try {
                  sourceSchemaName = parentNode.getExecutionUnit().getLocation().getPhysicalSchema().getSchemaName();
                } catch (Exception ex) {
                  sourceSchemaName = null;
                }
                Object srcCompBondObj = parentNode.getLogicalComponent().getBoundObject();
                sourceDataStore = ((oracle.odi.domain.model.OdiDataStore) srcCompBondObj).getRealObject()
            }

            // Publish any variables back into binding to make them available in templates and variableDefText
            void updateBinding() {
            	super.updateBinding()
                mBinding.setVariable( "option_TRUNCATE",      option_TRUNCATE )
                mBinding.setVariable( "intoTargetAST",        intoTargetAST )
                mBinding.setVariable( "targetTableAST",       targetTableAST )
            }

	        @ASTProducerMethod(processingType=ProcessingType.TARGET, producedASTType=HiveInsertStatement.class, styleKey="ProcessingStyleKey.Hive.INSERT")
            AbstractSyntaxTree INSERT( SqlQuery input ) {

                def returnAST
                def templateUtils = input.getTemplateUtils()

                // Create DML for insert into target table
                def targetTable      = getTargetTable()
                def targetColumnList = new ArrayList<Column>();
                def targetSelectList = new ArrayList<ISelectItem>();
                def targetSelectQuery = getSourceAndTargetExpressionsAndQuery( targetTable, input, targetSelectList, targetColumnList,
                        { attr ->
                        	// Is this an unmapped Attribute?
                        	if ( !attr.isMapped() ) {
                        	    // So we have to put a casted NULL
                        	    def hiveColumn = attr.getBoundObject()
                        	    def dataType   = hiveColumn.getDdlDataType()
                        	    return new UnmappedColumnExpression( dataType )
                        	}
                        	// Is this a constant value mapped on target?
                        	if ( !attr.referencesUpstreamAttribute()
                        	     && (attr.getExecuteOnHint() == MapExpression.ExecuteOnLocation.TARGET)
                        	   ) {
                        	   // So we just insert the verbatim expression text
                        	   return new StringExpression( attr.getExpression().getText(), attr )
                        	}
                        	// Ok, then this must be a mapped source column, which we just copy over
                        	return new CopyColumnExpression()
                        }  )

                intoTargetAST = new HiveInsertStatement( targetTable, targetColumnList, targetSelectQuery, ffFinder )
                initTargetAST( intoTargetAST )

                setDDLTargetTable(sourceDataStore, targetDataStore, targetTable, sourceSchemaName, false);

                // Create DDL for target
                targetTableAST   = new HiveCreateTableStatement( "abc", "def", targetTable, getHiveTechnology(), ffFinder )
                targetColumnList = physicalNode.getNodeAttributes().collect { attr -> Column.getColumn( targetTable, attr ) }
                targetTableAST.setColumns( targetColumnList )

                // Overwrite target data?
                intoTargetAST.overwrite = option_TRUNCATE

                // Publish variables into binding to make them available in templates and variableDefText
                updateBinding()

                //return intoTargetAST
                return intoTargetAST
              }
          }
